import React, { Component } from 'react';
import './App.css';
import Chart from "react-google-charts";

class Maps extends Component {

    // State to hold JSON data
    state = {
        items: [],
        isLoaded: false,
    }

    /**
     * Fetches data from API and loads state.
     */
    componentDidMount() {
        fetch('https://covidtracking.com/api/states/daily')
            .then(res => res.json())
            .then(data => {
                this.setState({
                    items: data,
                    isLoaded: true,
                })
            });
    }

    /**
     * Renders map using google chart
     *
     * @return {html} html to render.
     */
    render() {
        var { items, isLoaded } = this.state;

        // Check if 
        if (!isLoaded) {
            return <div>Loading...</div>
        }

        var array = items.map(function(item) {
            return Object.values(item);
          });

        var idxToDelete = [0,3,4,5,7];

        for (var i = 0; i < array.length; i++) {
            var temp = array[i];
            array[i] = [];
            for(var j = 0 ; j < temp.length ; j++){
                if(idxToDelete.indexOf(j) == -1) // dont delete
                {
                    array[i].push(temp[j]);
                }
            }
        }

        array.unshift(['State', 'Confirmed Cases', 'Tests Done']);

        var spliced = array.slice(0, 52);


        return (
            <div style={{ display: 'flex', maxWidth: 900 }}>
                <Chart
                    width={700}
                    height={500}
                    chartType="GeoChart"
                    loader={<div>Loading Chart</div>}
                    data={
                        spliced
                    }
                    options={{
                        region: 'US',
                        resolution: 'provinces',
                    }}

                />
            </div>
        )
    }
}

export default Maps;
